insert  into `pages`(`page_id`,`page_name`,`page_title`,`page_info`,`page_order`,`page_group`,`template`) values 
(1,'home','IQ - energy',' Рады приветствовать Вас на сайте компании <strong>«Innovation Qazaqstan Energy»</strong>.\r\n          С уважением к Вам и Вашему делу, Команда <strong>«IQ Energy» </strong> </p>',0,'home','index.jsp'),
(2,'company','О компании','<strong>Товарищество с ограниченной ответственностью «Innovation Qazaqstan Energy»</strong> - \r\nэнергичная и  креативная компания, сфокусированная на поиск и внедрение инновационных\r\n и интеллектуальных разработок, развитие альтернативных видов энергетики, подбор \r\nнаилучших доступных техник и  энергоэффективных технологий, содействие в реализации\r\n механизмов зеленой экономики с целью снижения уровня загрязнения окружающей среды \r\nи улучшения экологической ситуации в целом, а также повышения техники безопасности \r\nтруда и жизнедеятельности человека.\r\n\r\n\r\n<table class=\"table table-borderless\">\r\n  <tbody>	  \r\n <tr>\r\n\r\n<td  class=\"text-center\" > \r\n<br> <a href=\"/documents/company/Svidetelstvo_Distribiutera.jpg\"> скачать Cвидетельство Дистрибьютора </a>\r\n	  <br>\r\n<img src =\"/documents/company/Svidetelstvo_Distribiutera.jpg\" width=200 height=300 >\r\n\r\n</td>\r\n      \r\n\r\n    </tr>	  \r\n		  	   \r\n  </tbody>\r\n</table>',0,'company','company.jsp'),
(3,'rekvizit','Реквизиты компании','\r\n<table class=\"table table-borderless\">\r\n  <tbody>	  \r\n <tr>\r\n\r\n  <td  class=\"text-center\" > \r\n\r\n\r\n<img src =\"/documents/company/rekvizit3/rek1.jpg\" width=270 height=310 >\r\n\r\n</td>\r\n      \r\n<td class=\"text-center\">\r\n<img src =\"/documents/company/rekvizit3/rek2.jpg\" width=270 height=310  >\r\n</td>	 \r\n\r\n<td class=\"text-center\">\r\n<img src =\"/documents/company/rekvizit3/rek3.jpg\" width=270 height=310  >\r\n</td>	\r\n\r\n    </tr>	  \r\n		  	   \r\n  </tbody>\r\n</table>\r\n',5,'company','company.jsp'),
(4,'dilers','Дилеры и субдилеры','Раздел в разработке. ',1,'company','company.jsp'),
(5,'partners','Партнеры','<strong>ТОО «КЗМИ»</strong><br>\r\nКазахстанский завод магнезитовых изделий, открыт в 2016 году в городе Астане.<br>\r\n<br>\r\nТОО \"КЗМИ\"- первый и единственный отечественный товаропроизводитель, противогололедных\r\nматериалов и реагентов пылеподавления для нужд жилищно-коммунальных хозяйств и \r\nнедропользователей, горно-обогатительных комбинатов и топливно-энергетических комплексов,\r\nавиационной сферы и железнодорожных перевозок, а также автодорожной отрасли \r\nв Республике Казахстане.\r\n<br>\r\nСтратегическими клиентами и партнерами завода-изготовителя являются:\r\n<br>\r\n\r\n<table class=\"table table-borderless\">\r\n  <tbody>	  \r\n    <tr>\r\n      <td class=\"align-middle\" > <strong>ТОО «Астана-Тазалык»</strong> </td>\r\n      <td ><img src =\"/documents/company/partners5/astana_tazalyk.jpg\" width=200 height=100 ></td>	 \r\n    </tr>	  \r\n	 <tr>\r\n      <td class=\"align-middle\"><strong>АО «НК «КазАвтоЖол»</strong> </td>\r\n      <td> <img src =\"/documents/company/partners5/AO_QAZ_AVTOJOL.jpg\" width=200 height=100 > </td>	 \r\n    </tr>\r\n	  <tr>\r\n      <td class=\"align-middle\"> <strong>АО «Air Astana»</strong> </td>\r\n      <td >  <img src =\"/documents/company/partners5/Air_Astana.jpg\" width=200 height=100 > </td>	 \r\n    </tr>	  	   \r\n  </tbody>\r\n</table>	\r\n	',2,'company','company.jsp'),
